import React, { PureComponent, useState, useEffect } from 'react';
import {
  ComposedChart,
  Line,
  Area,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';
import * as moment from 'moment';
import Widget from "components/Widget/index";

import "../../../assets/styles/flip-card.css"

const data = [
    {name: 'Page A', uv: 4000, price: 2400, amt: 2400},
    {name: 'Page B', uv: 3000, price: 1398, amt: 2210},
    {name: 'Page C', uv: 2000, price: 9800, amt: 2290},
    {name: 'Page D', uv: 2780, price: 3908, amt: 2000},
    {name: 'Page E', uv: 1890, price: 4800, amt: 2181},
    {name: 'Page F', uv: 2390, price: 3800, amt: 2500},
    {name: 'Page G', uv: 3490, price: 4300, amt: 2100},
  ];

const GrafikPendapatanBulan = (props) => {
    const {latestTransaction, setLatestTransactio} = props

    const bulanIni = moment().format('YYYY-MM')
    const bulanLalu =moment().subtract(1,'months').format('YYYY-MM')

    const [moneyThisMonth, setMoneyThisMonth] = useState()
    const [moneyLastMonth, setMoneyLastMonth] = useState()

    const bulan = latestTransaction && latestTransaction.map(row => ({
      tanggal_transaksi: moment(row[1].stringValue).format('YYYY-MM'),

      nilai: row[3].stringValue,
    }));
    const objBulanIni = bulan && bulan.filter(o => o.tanggal_transaksi === bulanIni);
    const currentBulanIni = objBulanIni && objBulanIni.map(v => Number(v.nilai))
      .reduce((sum, current) => sum + current, 0)

      const objBulanLalu = bulan && bulan.filter(o => o.tanggal_transaksi === bulanLalu);
      const currentBulanLalu = objBulanLalu && objBulanLalu.map(v => Number(v.nilai))
        .reduce((sum, current) => sum + current, 0)    

        console.log("bulan",typeof bulan)
        console.log("data",typeof data)
        console.log("data", data)

    useEffect(() => {
      setMoneyThisMonth(currentBulanIni);
    }, [currentBulanIni]);

    useEffect(() => {
      setMoneyLastMonth(currentBulanLalu);
    }, [currentBulanLalu]);
    const formatter = new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',


    });
  return (
    <Widget 
    styleName="gx-order-history"
    // style={{backgroundColor:"red"}}
    //   title={
    //     <h2 style={{textAlign:"left"}} >
    //       10 Latest Transaction
    //     </h2>
    //   }

    >
        <ResponsiveContainer width="95%" height={300}>

    <ComposedChart
    data={bulan}
    margin={{
      top: 20,
      right: 20,
      bottom: 20,
      left: 20
    }}
  >
    <CartesianGrid stroke="#f5f5f5" />
    <XAxis dataKey="name" scale="band" />
    <YAxis />
    <Tooltip />
    <Legend />
    <Bar dataKey="uv" barSize={20} fill="#413ea0" />
    <Line type="monotone" dataKey="uv" stroke="#ff7300" />
  </ComposedChart>
  </ResponsiveContainer>

  </Widget>
  );
};

export default GrafikPendapatanBulan;
