import React, { useState } from "react";
import { Menu } from "antd";
import { Link } from "react-router-dom";

import CustomScrollbars from "util/CustomScrollbars";
import SidebarLogo from "./SidebarLogo";
import UserProfile from "./UserProfile";
import AppsNavigation from "./AppsNavigation";
import {
  NAV_STYLE_NO_HEADER_EXPANDED_SIDEBAR,
  NAV_STYLE_NO_HEADER_MINI_SIDEBAR,
  THEME_TYPE_LITE
} from "../../constants/ThemeSetting";
import IntlMessages from "../../util/IntlMessages";
import { useSelector } from "react-redux";
import {
  AppstoreOutlined,
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  PieChartOutlined,
  DesktopOutlined,
  ContainerOutlined,
  MailOutlined,
} from '@ant-design/icons';

const SidebarContent = ({ sidebarCollapsed, setSidebarCollapsed }) => {
  const { navStyle, themeType } = useSelector(({ settings }) => settings);
  const pathname = useSelector(({ common }) => common.pathname);
  const { SubMenu } = Menu;

  const [collapsed, setCollapsed] = useState(false)

  const toggleCollapsed = () => {
    setCollapsed({
      collapsed: !collapsed,
    });
  };
  const getNoHeaderClass = (navStyle) => {
    if (navStyle === NAV_STYLE_NO_HEADER_MINI_SIDEBAR || navStyle === NAV_STYLE_NO_HEADER_EXPANDED_SIDEBAR) {
      return "gx-no-header-notifications";
    }
    return "";
  };

  const selectedKeys = pathname.substr(1);
  const defaultOpenKeys = selectedKeys.split('/')[1];

  return (
    <>
      <SidebarLogo sidebarCollapsed={sidebarCollapsed} setSidebarCollapsed={setSidebarCollapsed} />
      <div className="gx-sidebar-content">
        <div className={`gx-sidebar-notifications ${getNoHeaderClass(navStyle)}`}>
          <UserProfile />
          <AppsNavigation />
        </div>
        <CustomScrollbars className="gx-layout-sider-scrollbar">
          <Menu
            defaultOpenKeys={[defaultOpenKeys]}
            selectedKeys={[selectedKeys]}
            theme={themeType === THEME_TYPE_LITE ? 'lite' : 'dark'}
            mode="inline"
            inlineCollapsed={collapsed}
          // defaultOpenKeys={['sub1']}

          >

            <Menu.Item key="dashboard">
              <Link to="/dashboard"><i className="icon icon-widgets" />
                <span><IntlMessages id="sidebar.samplePage" /></span>
              </Link>
            </Menu.Item>

            {/* <Menu.Item key="wajib-pajak">
              <Link to="/wajib-pajak"><i className="icon icon-widgets" />
                <span><IntlMessages id="sidebar.wajibPajak" /></span>
              </Link>
            </Menu.Item> */}
            <SubMenu key="sub1" icon={<MailOutlined />} title="Wajib Pajak">
              <Menu.Item key="register-wajib-pajak">
                <Link to="/register-wajib-pajak"><i className="icon icon-widgets" />
                  <span><IntlMessages id="sidebar.registerWajibPajak" /></span>
                </Link>
              </Menu.Item>               
              <Menu.Item key="register-wajib-pajak" icon={<OrderedListOutlined />} >
                <Link to="/register-wajib-pajak">
                  <span><IntlMessages id="sidebar.registerWajibPajak" /></span>
                </Link>
              </Menu.Item>
            </SubMenu>
          </Menu>
        </CustomScrollbars>
      </div>
    </>
  );
};

export default React.memo(SidebarContent);
