    {
        value: 'Hotel',
        label: 'Hotel',
    },
    {
        value: 'Restoran',
        label: 'Restoran',
    },
    {
        value: 'Parkir',
        label: 'Parkir',
    },
    {
        value: 'Minimarket',
        label: 'Minimarket',
    },
]

