import { useEffect, useState, useRef } from 'react';
import { httpClient } from "../../../util/Api";
import {useHistory} from "react-router-dom";

export const useProvideAuth = () => {
  const [authUser, setAuthUser] = useState(null);
  const [error, setError] = useState('');
  const [isLoadingUser, setLoadingUser] = useState(true);
  const [isLoading, setLoading] = useState(false);
  // const [dataAuth, setDataAuth] = useState()
  // const nRef = useRef(dataAuth);
  const history = useHistory();
  // console.log("authUser",authUser )


  // useEffect(() => { 
  //   nRef.current = dataAuth 
  // }, 
  //  [dataAuth]
  //  ) 
// console.log("nRef",nRef)

  const fetchStart = () => {
    setLoading(true);
    setError('');
  };

  const fetchSuccess = () => {
    setLoading(false);
    setError('');
  };

  const fetchError = (error) => {
    setLoading(false);
    setError(error);
  };

  const userLogin = (user, callbackFun) => {
    // console.log("user", user)

    // setDataAuth(user)
    fetchStart();
    httpClient
      .get('login?username=' + user.username + '&password=' + user.password, user)
      .then(({ data }) => {
        if (data) {
          fetchSuccess();
          httpClient.defaults.headers.common['Authorization'] = 'Bearer ' + data.access_token;
          localStorage.setItem('token', data.access_token);
          getAuthUser();
          if (callbackFun) callbackFun();
        } else {
          fetchError(data.error);
        }     
        //  console.log("data.result",data)

      })
      .catch(function (error) {
        // fetchError(error.message);
        fetchError("Username / Password Salah");

      });
  };


  const userSignup = (user, callbackFun) => {
    fetchStart();
    httpClient
      .post('auth/register', user)
      .then(({ data }) => {
        if (data) {
          fetchSuccess();
          localStorage.setItem('token', data.access_token);
          httpClient.defaults.headers.common['Authorization'] = 'Bearer ' + data.access_token;
          getAuthUser();
          if (callbackFun) callbackFun();
        } else {
          fetchError(data.error);
        }
      })
      .catch(function (error) {
        fetchError(error.message);
      });
  };

  const sendPasswordResetEmail = (username, callbackFun) => {
    fetchStart();

    setTimeout(() => {
      fetchSuccess();
      if (callbackFun) callbackFun();
    }, 300);
  };

  const confirmPasswordReset = (code, password, callbackFun) => {
    fetchStart();

    setTimeout(() => {
      fetchSuccess();
      if (callbackFun) callbackFun();
    }, 300);
  };

  const renderSocialMediaLogin = () => null;

  const userSignOut = (callbackFun) => {
    fetchStart();
    httpClient
      .get('/logout')
      .then(({ data }) => {
        if (data) {
          fetchSuccess();
          httpClient.defaults.headers.common['Authorization'] = '';
          localStorage.removeItem('token');
          setAuthUser(false);
          if (callbackFun) callbackFun();
        } else {
          fetchError(data.error);
        }
        // console.log("data logout 0000000000000000000", data)
      })
      .catch(function (error) {
        fetchError(error.message);
      });
      // history.push('/signin')
      };


  const getAuthUser = () => {
    fetchStart();
    httpClient
      .get('/user')
      .then(({ data }) => {
        if (data) {
          fetchSuccess();
          setAuthUser(data);
        } else {
          fetchError(data.error);
        }      
        // console.log("data user", data)

      })
      .catch(function (error) {
        httpClient.defaults.headers.common['Authorization'] = '';
        fetchError(error.message);
      });
  };

  // Subscribe to user on mount
  // Because this sets state in the callback it will cause any ...
  // ... component that utilizes this hook to re-render with the ...
  // ... latest auth object.

  useEffect(() => {
    const token = localStorage.getItem('token');
        if (token) {
        httpClient.defaults.headers.common['Authorization'] = 'Bearer ' + token;
      }
      if(token){
      httpClient
        // .get('login?username=' + nRef.username + '&password=' + nRef.password, nRef)
        .get('/user')
        .then(({ data }) => {
          if (data) {
            setAuthUser(data);
          }
          setLoadingUser(false);
          console.log("data user USEEFFECT", data)

        })
        .catch(function () {
          localStorage.removeItem('token');
          httpClient.defaults.headers.common['Authorization'] = '';
          setLoadingUser(false);
        })
      } else{
        console.log("tidak ada")
      }

  }, []);

  // Return the user object and auth methods
  return {
    isLoadingUser,
    isLoading,
    authUser,
    error,
    setError,
    setAuthUser,
    getAuthUser,
    userLogin,
    userSignup,
    userSignOut,
    renderSocialMediaLogin,
    sendPasswordResetEmail,
    confirmPasswordReset,
  };
};
