import React, { useState, useEffect } from "react";
import { Col, Row } from "antd";
import { Bar, BarChart, ResponsiveContainer, Tooltip } from "recharts";
import Widget from "../../components/Widget/index"
import EcommerceStatus from "../../components/Metrics/EcommerceStatus";
import * as moment from 'moment';
import { forkJoin, mergeMap } from 'rxjs';
import "../../assets/styles/flip-card.css"

const PendapatanBulanIni = (props) => {
  const {latestTransaction, setLatestTransaction, getLatestTransaction} = props
  const bulanIni = moment().format('YYYY-MM')
  const bulanLalu =moment().subtract(1,'months').format('YYYY-MM')

  const [moneyThisMonth, setMoneyThisMonth] = useState()
  const [moneyLastMonth, setMoneyLastMonth] = useState()

  const bulan = latestTransaction && latestTransaction.map(row => ({
    nilai: row[3].stringValue,
    tanggal_transaksi: moment(row[1].stringValue).format('YYYY-MM')
  }));
  const objBulanIni = bulan && bulan.filter(o => o.tanggal_transaksi === bulanIni);
  const currentBulanIni = objBulanIni && objBulanIni.map(v => Number(v.nilai))
    .reduce((sum, current) => sum + current, 0)

    const objBulanLalu = bulan && bulan.filter(o => o.tanggal_transaksi === bulanLalu);
    const currentBulanLalu = objBulanLalu && objBulanLalu.map(v => Number(v.nilai))
      .reduce((sum, current) => sum + current, 0)    
console.log("bulanLalu",bulanLalu)
  console.log("objBulanLalu", objBulanLalu);
  console.log("currentBulanLalu", currentBulanLalu);

  useEffect(() => {
    setMoneyThisMonth(currentBulanIni);
  }, [currentBulanIni]);
  
  useEffect(() => {
    setMoneyLastMonth(currentBulanLalu);
  }, [currentBulanLalu]);

  return (
    <Col xl={4} lg={8} md={8} sm={12} xs={24} 
    className="e-commerce-status" >
      <div className="flip-card" >
        <div className="flip-card-inner" >
          <div className="flip-card-front" >
          <EcommerceStatus color="green"
  icon="revenue-new" title={formatter.format(currentBulanIni)} colorTitle="black" 
  moneyThisMonth={moneyThisMonth} setMoneyThisMonth ={setMoneyThisMonth} moneyLastMonth={moneyLastMonth}  setMoneyLastMonth={setMoneyLastMonth} objBulanIni={objBulanIni}
              subTitle="Total Pendapatan (Hari Ini)" colorSubTitle="black"  />
          </div>
          <div class="flip-card-back">
            <EcommerceStatus color="grey" icon="revenue-new" title={formatter.format(currentBulanLalu)} colorTitle="black" 
              // moneyToday={moneyToday} setMoneyToday ={setMoneyToday} moneyYesterday={moneyYesterday} setMoneyYesterday={setMoneyYesterday}
              subTitle="Total Pendapatan (Kemarin)" colorSubTitle="black"/>
        </div>
        </div>
      </div>
    </Col>
  );
};

export default PendapatanBulanIni;
