import React, { useState, useEffect } from "react";
import { Col, Row } from "antd";
import { Bar, BarChart, ResponsiveContainer, Tooltip } from "recharts";
import EcommerceStatus from "../../../components/Metrics/EcommerceStatus";
import * as moment from 'moment';

const TransaksiBulanIni = (props) => {
  const { latestTransaction, setLatestTransaction } = props
  const [transactionThisMonth, setTransactionThisMonth] = useState()
  const [transactionLastMonth, setTransactionLastMonth] = useState()

  const now = moment().format('YYYY-MM-DD')
  const kemarin = moment().subtract(1, 'd').format('YYYY-MM-DD')
  const tanggal = latestTransaction && latestTransaction.map(row => ({
    nilai: row[3].stringValue,
    tanggal_transaksi: moment(row[1].stringValue).format('YYYY-MM-DD')
  }));

  const data = latestTransaction && latestTransaction.map(row => ({
    transaction_id: row[0].stringValue, merchant_id: row[2].stringValue,
    transaction_value: row[3].stringValue, tanggal_transaksi: row[1].stringValue
  }));
  const objTransactionThisMonth = tanggal && tanggal.filter(o => o.tanggal_transaksi === now);
  console.log("obj", objTransactionThisMonth);


  const transaksiHariIni = objTransactionThisMonth.length
  // console.log("currentVal", currentVal)
  const objTransactionLastMonth = tanggal && tanggal.filter(o => o.tanggal_transaksi === kemarin);
  const transaksiKemarin = objTransactionLastMonth.length

  console.log("transactionThisMonth *******",transactionThisMonth )
  console.log("transactionLastMonth *******",transactionLastMonth )

  useEffect(() => {
    setTransactionThisMonth(transaksiHariIni);
  }, [transaksiHariIni]);
  
  useEffect(() => {
    setTransactionLastMonth(transaksiKemarin);
  }, [transaksiKemarin]);


  return (
    <Col xl={4} lg={8} md={8} sm={12} xs={24}>
   
  <div className="flip-card" >
        <div className="flip-card-inner" >
          <div className="flip-card-front" >
          <EcommerceStatus color="orange"
  icon="orders" title={<div style={{marginBottom:"35px"}}>{transaksiHariIni}</div>} colorTitle="geekblue" 
  transactionThisMonth={transactionThisMonth} setTransactionThisMonth ={setTransactionThisMonth} transactionLastMonth={transactionLastMonth}  setTransactionLastMonth={setTransactionLastMonth} objTransactionThisMonth={objTransactionThisMonth}
              subTitle="Total Transaksi (hari Ini)" colorSubTitle="geekblue"  />

{/* <EcommerceStatus color="orange" icon="orders" title="4" colorTitle="geekblue"
                     subTitle="Total Transaksi (Hari Ini)" colorSubTitle="geekblue"/> */}
          </div>
          <div class="flip-card-back">
            <EcommerceStatus  icon="orders" color="grey"  title={<div style={{marginBottom:"115px"}}>{transaksiKemarin}</div>} colorTitle="dark" 
              subTitle="Total Transaksi (Kemarin)" colorSubTitle="dark"/>
        </div>
        </div>
      </div>
  </Col>
  );
};

export default TransaksiBulanIni;
