import { useEffect, useState, useRef } from 'react';
import { httpClient } from "../../../util/Api";

export const useProvideAuth = () => {
  const [authUser, setAuthUser] = useState(null);
  const [error, setError] = useState('');
  const [isLoadingUser, setLoadingUser] = useState(true);
  const [isLoading, setLoading] = useState(false);
  const [Namaa, setNamaa] = useState()
  const nRef = useRef(Namaa);


  useEffect(() => { nRef.current = Namaa }) 
console.log("nRef",nRef)

  const fetchStart = () => {
    setLoading(true);
    setError('');
  };

  const fetchSuccess = () => {
    setLoading(false);
    setError('');
  };

  const fetchError = (error) => {
    setLoading(false);
    setError(error);
  };

  const userLogin = (user, callbackFun) => {
    console.log("user", user)
    setNamaa(user)
    fetchStart();
    httpClient
    // .post('auth/login', user)

      .get('login?username=' + user.username + '&password=' + user.password, user)
      .then(({ data }) => {
        if (data) {
          fetchSuccess();
          httpClient.defaults.headers.common['Authorization'] = 'Bearer ' + data.access_token;
          localStorage.setItem('token', data.access_token);
          getAuthUser();
          if (callbackFun) callbackFun();
        } else {
          fetchError(data.error);
        }     
         console.log("data.result",data)

      })
      .catch(function (error) {
        fetchError(error.message);
      });
  };
  // const logh  = (user, callbackFun) => {
  // console.log("usert", user)
  // const AAA = `login?username=`+user.username+`&password=`+user.password

  // // return user
  // }

  // console.log("Log", logh())
  const userSignup = (user, callbackFun) => {
    fetchStart();
    httpClient
      .post('auth/register', user)
      .then(({ data }) => {
        if (data.result) {
          fetchSuccess();
          localStorage.setItem('token', data.access_token);
          httpClient.defaults.headers.common['Authorization'] = 'Bearer ' + data.access_token;
          getAuthUser();
          if (callbackFun) callbackFun();
        } else {
          fetchError(data.error);
        }
      })
      .catch(function (error) {
        fetchError(error.message);
      });
  };

  const sendPasswordResetEmail = (username, callbackFun) => {
    fetchStart();

    setTimeout(() => {
      fetchSuccess();
      if (callbackFun) callbackFun();
    }, 300);
  };

  const confirmPasswordReset = (code, password, callbackFun) => {
    fetchStart();

    setTimeout(() => {
      fetchSuccess();
      if (callbackFun) callbackFun();
    }, 300);
  };

  const renderSocialMediaLogin = () => null;

  const userSignOut = (callbackFun) => {
    fetchStart();
    httpClient
      .post('auth/logout')
      .then(({ data }) => {
        if (data.result) {
          fetchSuccess();
          httpClient.defaults.headers.common['Authorization'] = '';
          localStorage.removeItem('token');
          setAuthUser(false);
          if (callbackFun) callbackFun();
        } else {
          fetchError(data.error);
        }
      })
      .catch(function (error) {
        fetchError(error.message);
      });
  };
  // useEffect(() => {
  //   /* Assign update to outside variable */
  //   kamu = Namaa

  //   /* Unassign when component unmounts */
  //   return () => kamu = null
  // }, [kamu])
  console.log("AUTHHH USER",authUser )

  const getAuthUser = (kamu) => {
    fetchStart();
    // let kamu = Namaa
    console.log("thisthisthisthisthisthis", Namaa)

    httpClient
      // .post('/login')
      .get('/user')

      // .get('login?username=' + Namaa.username + '&password=' + Namaa.password, Namaa)
      // .post('auth/me')
      .then(({ data }) => {
        if (data) {
          fetchSuccess();
          setAuthUser(data);
        } else {
          fetchError(data.error);
        }      console.log("data user", data)

      })
      .catch(function (error) {
        httpClient.defaults.headers.common['Authorization'] = '';
        fetchError(error.message);
      });
  };

  // Subscribe to user on mount
  // Because this sets state in the callback it will cause any ...
  // ... component that utilizes this hook to re-render with the ...
  // ... latest auth object.

  useEffect(() => {
    const token = localStorage.getItem('token');
    // const user = localStorage.getItem('user');
    // setUsery({ Namaa })
    console.log("sssfdsf", Namaa)
      // if (token && Namaa) {
        if (token) {

        httpClient.defaults.headers.common['Authorization'] = 'Bearer ' + token;
      }
      httpClient

        .get('login?username=' + nRef.username + '&password=' + nRef.password, nRef)

        .then(({ data }) => {
          if (data) {
            setAuthUser(data.user);
          }
          setLoadingUser(false);
        })
        .catch(function () {
          localStorage.removeItem('token');
          httpClient.defaults.headers.common['Authorization'] = '';
          setLoadingUser(false);
        })

  }, [], [Namaa]);

  // Return the user object and auth methods
  return {
    isLoadingUser,
    isLoading,
    authUser,
    error,
    setError,
    setAuthUser,
    getAuthUser,
    userLogin,
    userSignup,
    userSignOut,
    renderSocialMediaLogin,
    sendPasswordResetEmail,
    confirmPasswordReset,
  };
};
