import React, { useState ,useForm} from "react";
import { Form, Button, Steps, Card } from "antd";

const StepPanel = (props) => {

const {activeStep, setActiveStep ,stepForm} =props
// const [form] = stepForm.useForm();

  function next() {
    const nextStep = activeStep + 1;
    setActiveStep(nextStep);
    // form
    // .validateFields()
    // .then(() => {
    //   // Here make api call of something else
    //   setActiveStep(activeStep + 1);
    // })
    // .catch((err) => console.log(err));
  }

  function prev() {
    const prevStep = activeStep - 1;
    setActiveStep(prevStep);
  }

  return (
    <>

      <Steps activeStep={activeStep} style={{ width: "100%" }}>
        {props.steps.map((item) => (
          <Steps.Step key={item.title} title={item.title} description={item.description} />
        ))}
      </Steps>
      <div className="steps-content">{props.steps[activeStep].content}</div>
      <div className="steps-action">
        {/* {activeStep < props.steps.length - 1 && ( */}
                    {          activeStep === 0 ? "activeStep" : activeStep > 0 ? "wait" : "process" && (

          <Button type="primary" onClick={() => next()}>
            Next
          </Button>
        )}
        {/* {activeStep === props.steps.length - 1 && ( */}
          {          activeStep === 1 ? "activeStep" : activeStep >1 ? "wait" : "process" && (
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        )}
        {/* {activeStep > 0 && ( */}
          {          activeStep === 2 ? "activeStep" : activeStep > 2 ? "wait" : "process" && (

          <Button  onClick={() => prev()}>
            Previous
          </Button>
        )}
      </div>
    </>
  );
};

export { StepPanel };
