import React, { useState, useEffect } from "react";
import { Col, Row } from "antd";
import { Bar, BarChart, ResponsiveContainer, Tooltip } from "recharts";
import EcommerceStatus from "../../../components/Metrics/EcommerceStatus";
import * as moment from 'moment';

const TransaksiTahunIni = (props) => {
  const { latestTransaction, setLatestTransaction } = props
  const [transactionThisYear, setTransactionThisYear] = useState()
  const [transactionLastYear, setTransactionLastYear] = useState()

  const tahunIni = moment().format('YYYY-MM-DD')
  const tahunLalu = moment().subtract(1, 'd').format('YYYY-MM-DD')
  const tanggal = latestTransaction && latestTransaction.map(row => ({
    nilai: row[3].stringValue,
    tanggal_transaksi: moment(row[1].stringValue).format('YYYY-MM-DD')
  }));

  const data = latestTransaction && latestTransaction.map(row => ({
    transaction_id: row[0].stringValue, merchant_id: row[2].stringValue,
    transaction_value: row[3].stringValue, tanggal_transaksi: row[1].stringValue
  }));
  const objTransactionThisYear = tanggal && tanggal.filter(o => o.tanggal_transaksi === tahunIni);
  console.log("obj", objTransactionThisYear);


  const transaksiTahunIni = objTransactionThisYear.length
  // console.log("currentVal", currentVal)
  const objTransactionLastYear = tanggal && tanggal.filter(o => o.tanggal_transaksi === tahunLalu);
  const transaksiTahunLalu = objTransactionLastYear.length

  console.log("transactionThisYear *******",transactionThisYear )
  console.log("transactionLastYear *******",transactionLastYear )

  useEffect(() => {
    setTransactionThisYear(transaksiTahunIni);
  }, [transaksiTahunIni]);
  
  useEffect(() => {
    setTransactionLastYear(transaksiTahunLalu);
  }, [transaksiTahunLalu]);


  return (
    <Col xl={4} lg={8} md={8} sm={12} xs={24}>
   
  <div className="flip-card" >
        <div className="flip-card-inner" >
          <div className="flip-card-front" >
          <EcommerceStatus color="light"
  icon="orders" title={<div style={{marginBottom:"35px"}}>{transaksiTahunIni}</div>} colorTitle="geekblue" 
  transactionThisYear={transactionThisYear} setTransactionThisYear ={setTransactionThisYear} transactionLastYear={transactionLastYear}  setTransactionLastYear={setTransactionLastYear} objTransactionThisYear={objTransactionThisYear}
              subTitle="Total Transaksi (Tahun Ini)" colorSubTitle="geekblue"  />

{/* <EcommerceStatus color="orange" icon="orders" title="4" colorTitle="geekblue"
                     subTitle="Total Transaksi (Hari Ini)" colorSubTitle="geekblue"/> */}
          </div>
          <div class="flip-card-back">
            <EcommerceStatus  icon="orders" color="grey"  title={<div style={{marginBottom:"115px"}}>{transaksiTahunLalu}</div>} colorTitle="dark" 
              subTitle="Total Transaksi (Tahun Lalu)" colorSubTitle="dark"/>
        </div>
        </div>
      </div>
  </Col>
  );
};

export default TransaksiTahunIni;
