import React, { useState, useEffect } from "react";
import {
    PageHeader, Form, Input, Card, Cascader, Steps, Button, Select
} from "antd";
import { useHistory } from "react-router-dom";

import { StepPanel } from "./stepPanel";
import { options } from "less";
import DataProvinsi from "./dataProvinsi";
import DataKabupaten from "./dataKabupaten";
import DataKecamatan from "./dataKecamatan";
const { Option } = Select;

const { Step } = Steps;
// const { Item } = Form;

const kategori = [
    {
        value: 'Hotel',
        label: 'Hotel',
    },
    {
        value: 'Restoran',
        label: 'Restoran',
    },
    {
        value: 'Parkir',
        label: 'Parkir',
    },
    {
        value: 'Minimarket',
        label: 'Minimarket',
    },
]

function MyStepForm() {

    const history = useHistory();
    const [dataSumber, setDataSumber] = useState()
    const [category, setCategory] = useState()

    const [provinceId, setProvinceId] = useState(null)
    const [prov, setProv] = useState("")
    const [errorProv, setErrorProv] = useState(false);

    const [current, setCurrent] = useState(0);


    const [kabKotaId, setKabKotaId] = useState(null)
    const [kab, setKab] = useState("")
    const [kec, setKec] = useState("")
    const [kel, setKel] = useState("")
    const [alamatDetil, setAlamatDetil] = useState("")
    const [alamatLengkap, setAlamatLengkap] = useState("")

    console.log("alamatLengkap", alamatLengkap)
    const [regisPajak, setRegisPajak] = useState(
        {
            merchant_id: "",
            owner: "",
            nik: "",
            email: "",
            nama_usaha: "",
            alamat: "",
            kategori: "",
            data_source: dataSumber
        }

    );

    console.log("errorProv ======", errorProv)
    console.log("current ======", current)

    const [domisili_kabupaten, setDomisili_kabupaten] = useState()
    const prov_kab = JSON.stringify(domisili_kabupaten)
    const [form] = Form.useForm();
    useEffect(
        () => {
            setRegisPajak({
                ...regisPajak,
                alamat: alamatDetil + " " + kel + " " + kec + " " + kab + " " + prov
            });
        },
        [alamatDetil], [prov]
    );
    useEffect(
        () => {
            setRegisPajak({
                ...regisPajak,
                kategori: category
            });
        },
        [category]
    );
    useEffect(
        () => {
            setRegisPajak({
                ...regisPajak,
                data_source: dataSumber
            });
        },
        [dataSumber]
    );
    const handleClickNext = () => {

        if (current === 0 ) {
            form.validateFields()
                .then(() => {
                    // Here make api call of something else
                    setCurrent(current + 1);
                })
                .catch((err) => console.log(err));
        }
        else if (current === 1 && prov) {

            form
                .validateFields()
                .then(() => {
                    // Here make api call of something else
                    setCurrent(current + 1);
                })
                .catch((err) => console.log(err));
        }

        if (current === 1 && prov) {
            setErrorProv("");

        } else {
            setErrorProv(
                "   ❌ Pilih Provinsi terlebih dulu"
              );

        }

    };
    const handleClickPrev = () => {
        form
            .validateFields()
            .then(() => {
                // Here make api call of something else
                setCurrent(current - 1);
            })
            .catch((err) => console.log(err));
    };
    const handleChange = (e) => {
        setRegisPajak({ ...regisPajak, [e.target.name]: e.target.value });

    };

    const handleAlamat = (event) => {
        setAlamatDetil(event.target.value)
    }

    const handleChangeSelect = (value) => {
        setDataSumber(value);
    };
    const handleChangeKategori = (value) => {
        setCategory(value);
    };
    const handleFinish = async (values) => {
        try {
            const token = localStorage.getItem('token')
            const response = await fetch(
                "https://api.raspi-geek.com/v1/merchants",
                {
                    method: "POST",
                    headers: {
                        'x-api-key': '45N0Ujae7p9AWDs3ihDiU4ZDnvAMb9ZK4TXH0kCk',
                        'content-type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify(regisPajak),
                }
            );
            // const res = await response.json();
            history.push("/dashboard")
        } catch (err) {
            // console.log("error", err.message);
        }

    };


    return (
        <div style={{ width: "100%", margin: "40px auto" }}>
            <Steps current={current}>
                <Step key={0} title="Data Usaha" />
                <Step key={1} title="Alamat Usaha" />
                <Step key={2} title="Kategori Usaha" />
                <Step key={3} title="Selesai" />

            </Steps>
            <div style={{ margin: "100px 10px" }}>
                <Form
                    form={form}
                    // onSubmit={handleSubmit}
                    onFinish={handleFinish}
                    initialValues={{
                        merchant_id: "", owner: "", nik: "", email: "", nama_usaha: "",
                        kelurahan: "", alamat: "", kategori: "", data_source: ""

                    }}
                >

                    {current === 0 && (
                        <div style={{ width: "90%" }} >
                            <Form.Item
                                style={{ margin: "40px" }}
                                name="merchant_id"
                                label="Merchant ID"
                                rules={[

                                    {
                                        required: true,
                                        message: 'IMEI atau id dari POS APP',
                                    },
                                ]}
                            >
                                <Input
                                    name="merchant_id"
                                    value={regisPajak.merchant_id}
                                    onChange={handleChange}
                                    placeholder="IMEI atau id dari POS APP" style={{ width: "69%", margin: "0 0 0 10px" }} />
                            </Form.Item>
                            <Form.Item
                                style={{ margin: "40px" }}
                                name="owner"
                                label="Owner"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Nama owner dari usaha',
                                    },
                                ]}
                            >
                                <Input
                                    name="owner"
                                    value={regisPajak.owner}
                                    onChange={handleChange}
                                    placeholder="Nama Owner dari usaha" style={{ width: "64%", margin: "0 0 0 45px" }} />
                            </Form.Item>
                            <Form.Item
                                style={{ margin: "40px" }}
                                name="nik"
                                label="NIK"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Nomor Induk Kepegawaian',
                                    },
                                ]}
                            >
                                <Input
                                    name="nik"
                                    value={regisPajak.nik}
                                    onChange={handleChange}
                                    placeholder="Nomor Induk Kepegawaian" style={{ width: "61%", margin: "0 0 0 65px" }} />
                            </Form.Item>
                            <Form.Item
                                style={{ margin: "40px" }}
                                name="email"
                                label="Email"
                                rules={[
                                    {
                                        type: 'email',
                                        message: 'Masukkan E-mail!',
                                    },
                                    {
                                        required: true,
                                        message: 'someone@someplace.com',
                                    },
                                ]}
                            >
                                <Input
                                    name="email"
                                    value={regisPajak.email}
                                    onChange={handleChange}
                                    placeholder="someone@someplace.com" style={{ width: "63%", margin: "0 0 0 52px" }} />
                            </Form.Item>
                            <Form.Item
                                style={{ margin: "40px" }}
                                name="nama_usaha"
                                label="Nama Usaha"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Brand dari usaha',
                                    },
                                ]}
                            >
                                <Input
                                    name="nama_usaha"
                                    value={regisPajak.nama_usaha}
                                    onChange={handleChange}
                                    placeholder="Brand dari usaha" style={{ width: "70%", margin: "0 0 0 3px" }} />
                            </Form.Item>
                        </div>
                    )}
                    {current === 1 && (
                        <div style={{ width: "90%", margin: "0 0 40px 0" }} >
                            <DataProvinsi prov={prov} setProv={setProv} errorProv={errorProv} setErrorProv={setErrorProv} provinceId={provinceId} setProvinceId={setProvinceId} />
                            {prov && (
                                <DataKabupaten kab={kab} setKab={setKab} provinceId={provinceId} kabKotaId={kabKotaId} setKabKotaId={setKabKotaId} />
                            )}
                            {kab && (
                                <DataKecamatan kel={kel} setKel={setKel} kec={kec} setKec={setKec} kabKotaId={kabKotaId} />
                            )}
                            {kel && (
                                <div style={{ margin: "40px 0 40px 0" }} >
                                    <h4 style={{ margin: "40px 0 20px 0", color: "#53586D" }}>Alamat Detail</h4>
                                    {/* <Form.Item
                                style={{ margin: "0 0 40px 0" }}
                                name="alamat"
                                // label="Alamat"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Alamat',
                                    },
                                ]}
                            > */}
                                    <Input
                                        name="alamat"
                                        value={alamatDetil}
                                        required
                                        onChange={handleAlamat}
                                        placeholder="Jalan, RT RW"
                                        style={{ width: "100%" }} />
                                    {/* </Form.Item> */}
                                </div>
                            )}
                        </div>
                    )}
                    {current === 2 && (
                        <div style={{ width: "90%" }} >
                            <div style={{ margin: "40px 0" }} >
                                <h4 style={{ margin: "0px 0 20px 0", color: "#53586D" }}>Kategori Usaha</h4>
                                <Select
                                    // defaultValue="lucy" 
                                    style={{ margin: "40px 0 0 0" }}
                                    name="category"
                                    value={category}
                                    onChange={handleChangeKategori}
                                    rules={[
                                        { type: 'array', required: true, message: 'Pilih Kategori Usaha' },
                                    ]}
                                    style={{ width: "100%" }} >
                                    <Option value="Hotel">Hotel</Option>
                                    <Option value="Restoran">Restoran</Option>
                                    <Option value="Parkir">Parkir</Option>
                                    <Option value="Minimarket">Minimarket</Option>

                                </Select>
                            </div>
                            <div style={{ margin: "40px 0" }} >
                                <h4 style={{ margin: "0px 0 20px 0", color: "#53586D" }}>Sumber Data</h4>
                                <Select
                                    // defaultValue="lucy" 
                                    style={{ margin: "40px 0 0 0" }}
                                    name="dataSumber"
                                    value={dataSumber}
                                    onChange={handleChangeSelect}
                                    style={{ width: "100%" }} >
                                    <Option value="POS App">POS App</Option>
                                    <Option value="PDC">PDC</Option>
                                    <Option value="BDC">BDC</Option>
                                    <Option value="SDC">SDC</Option>

                                </Select>
                            </div>
                        </div>
                    )}


                    {current < 3 && (
                        <div style={{ textAlign: "center" }}>
                            <Button type="primary" onClick={handleClickNext}>Next step</Button>
                        </div>
                    )}
                    {current === 3 && (
                        <div style={{ textAlign: "center" }}>
                            <Button type="primary"
                                onClick={() => form.submit()}
                            >Submit</Button>
                        </div>
                    )}
                    {current < 3 && (
                        <div style={{ textAlign: "center" }}>
                            <Button onClick={handleClickPrev}>Previous step</Button>
                        </div>
                    )}
                </Form>

            </div>

        </div>
    );
}
export { MyStepForm };
