import React, { useState, useEffect } from "react";
import { Select } from "antd";
const { Option } = Select;

function DataKelurahan(props) {
    const { kecId, kelId, setKelId } = props
    const [idKel, setIdKel] = useState({})
    const [kelurahan, setKelurahan] = useState([{}])
    const [kel, setKel] = useState()

    console.log("kelId", kelId)
    console.log("idKec", idKel)

    console.log("kelurahan", kelurahan)
    // useEffect(
    //     () => {
    //         setKelId(
    //             idKel.key
    //         )
    //     },
    //     [idKel.key]
    // );
    useEffect(() => {
        getKelurahan();
    }, [kecId]);
    const getKelurahan = async () => {
        console.log("============", kecId.key)
        const response = await
            fetch(`https://dev.farizdotid.com/api/daerahindonesia/kelurahan?id_kecamatan=${kecId.key}`)
                .then(response => response.json())
                .then(res => {
                    setKelurahan({ data: res.kelurahan })

                })
        console.log("respoKelurahan", response)

    };

    const handleChangeKelurahan = (value, key) => {
        setKel(value);
        setKelId(key)
    };

    return (
        <div>
            <h4 style={{ margin: "20px 0 20px 0", color: "#53586D" }}>Kelurahan</h4>

            <Select
                style={{ margin: "40px 0 0 0" }}
                name="kelurahan"
                onChange={handleChangeKelurahan}
                value={kel}
                rules={[
                    { type: 'array', required: true, message: 'Pilih Kelurahan Yang Ada' },
                ]}
                placeholder="Pilih Kelurahan"
                style={{ width: "100%" }} >
                {kelurahan.data?.map((item) => (
                    <Option key={item.id}  >{item.nama}</Option>
                ))}
            </Select>

        </div>
    );
}

export default DataKelurahan;
