import React, { Component } from "react";
import EventsTable from "./EventTable";

// import styles from "./style.module.css";
// import  EventsTable  from "./EventsTable";
// import { StatusFilter } from "../StatusFilter";
import  TitleSearch  from "./TitleSearch";

const eventsData = [
  {
    key: 1,
    title: "Bulletproof EP1",
    fileType: "Atmos",
    process: "match media",
    performedBy: "Denise Etridge",
    operationNote: "-",
    updatedAt: "26/09/2018 17:21",
    status: "complete"
  },
  {
    key: 2,
    title: "Dexter EP2",
    fileType: "Video",
    process: "Compliance",
    performedBy: "Dane Gill",
    operationNote: "passed",
    updatedAt: "21/09/2018 12:21",
    status: "inProgress"
  }
];

class EventsSection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      eventsData
    };
  }

  handleFilter = key => {
    const selected = parseInt(key);
    if (selected === 3) {
      return this.setState({
        eventsData
      });
    }

    const statusMap = {
      1: "complete",
      2: "inProgress"
    };

    const selectedStatus = statusMap[selected];

    const filteredEvents = eventsData.filter(
      ({ status }) => status === selectedStatus
    );
    this.setState({
      eventsData: filteredEvents
    });
  };

  handleSearch = searchText => {
    const filteredEvents = eventsData.filter(({ title }) => {
      title = title.toLowerCase();
      return title.includes(searchText);
    });

    this.setState({
      eventsData: filteredEvents
    });
  };

  render() {
    return (
      <section  
    //   className={styles.container}
    >
        <header 
        // className={styles.header}
        >
          <h1 
        //   className={styles.title}
        >Events</h1>
          {/* <StatusFilter
            filterBy={this.handleFilter}
            className={styles.action}
          /> */}
          <TitleSearch onSearch={this.handleSearch} className={styles.action} />
        </header>

        <EventsTable eventsData={this.state.eventsData} />
      </section>
    );
  }
}

export default EventsSection ;
