    .reduce((sum, current) => sum + current, 0)

  const objBulanFebruari = bulan && bulan.filter(o => o.tanggal_transaksi === bulanFebruari);
  const currentBulanLalu = objBulanFebruari && objBulanFebruari.map(v => Number(v.nilai))
    .reduce((sum, current) => sum + current, 0)

  console.log("objBulanJanuari",  objBulanJanuari)
  console.log("objBulanFebruari", objBulanFebruari)
  console.log("bulanJanuari", bulanJanuari)
  console.log("dataBulan ***************", dataBulan)

  useEffect(() => {
    setDataBulan([
      {...dataBulan, 
