    setMoneyLastMonth(currentBulanLalu);
  }, [currentBulanLalu]);
  const formatter = new Intl.NumberFormat('id-ID', {
    style: 'currency',
    currency: 'IDR',


  });
  return (
    <Widget
      styleName="gx-order-history"
    // style={{backgroundColor:"red"}}
