    >
      <ResponsiveContainer width="95%" height={300}>

        <ComposedChart
          data={bulan}
          margin={{
            top: 20,
            right: 20,
            bottom: 20,
            left: 20
          }}
        >
          <CartesianGrid stroke="#f5f5f5" />
          <XAxis dataKey="tanggal_transaksi" scale="band" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="nilai" barSize={20} fill="#413ea0" />
          <Line type="monotone" dataKey="nilai" stroke="#ff7300" />
