import React, { useState, useEffect } from "react";
import { Table } from "antd";
import Widget from "components/Widget/index";
import reqOptions from "../../util/reqOptions";
// import { TokenStorageService } from './token-storage.service';
import * as moment from 'moment';

import jwtDecode from 'jwt-decode';

const columns = [
  {
    title: 'Transaction ID',
    dataIndex: 'transaction_id',
    // render: (text) => {
    //   return <span className="gx-text-red">{text}</span>
    // },
  },
  {
    title: 'Merchant ID	',
    dataIndex: 'merchant_id',
  },

  {
    title: 'Transaction Value	',
    dataIndex: 'transaction_value',
  },
  {
    title: 'Tanggal Transaksi	',
    dataIndex: 'tanggal_transaksi',
  },
];

const LatestTransaction = () => {
  const [latestTransaction, setLatestTransaction] = useState()
  const [loading, setloading] = useState(true);
  // console.log("latestTransaction", latestTransaction)
  useEffect(() => {
    getLatestTransaction();
  }, []);
  const now = moment().format('YYYY-MM-DD HH:mm:ss')
  const sToday = moment().startOf('day').format('YYYY-MM-DD HH:mm:ss')
  const eToday = moment().endOf('day').format('YYYY-MM-DD HH:mm:ss')
  const sYesterday = moment().subtract(1,'d').startOf('day').format('YYYY-MM-DD HH:mm:ss')
  const eYesterday = moment().subtract(1,'d').endOf('day').format('YYYY-MM-DD HH:mm:ss')
  
  const data = latestTransaction && latestTransaction.map(row => ({
    transaction_id: row[0].stringValue, merchant_id: row[2].stringValue,
    transaction_value: row[3].stringValue, tanggal_transaksi: row[1].stringValue
  }));
console.log("latestTransaction >>>>>>>>>>>>>>>>>>", latestTransaction)


// Tanggal 27 Total = 63.000
// Tanggal 23 Total = 175.000
// Tanggal 22 Total = 26.000.000
// Total = 26.238.000
// Total = 26.238.000


const currentVal=latestTransaction && latestTransaction .map( v => Number(v[3].stringValue) )                                
.reduce( (sum, current) => sum + current, 0 )
// const length = latestTransaction && latestTransaction .map( v => v[3].stringValue).length()                                

console.log("length",  latestTransaction &&  latestTransaction.length);
console.log("currentVal", currentVal);
console.log("now", now)
console.log("eToday", eToday)
console.log("sYesterday", sYesterday)
console.log("eYesterday", eYesterday)



  const getLatestTransaction = async (dataLatest) => {

    const token = localStorage.getItem('token')
    const headers = {
      'x-api-key': '45N0Ujae7p9AWDs3ihDiU4ZDnvAMb9ZK4TXH0kCk',
      'content-type': 'application/json',
      'Authorization': `Bearer ${token}`
    }

    const response = await fetch(
      "https://api.raspi-geek.com/v1/latestorder",

      { method: "GET", headers }
    );
    const ajson = await response.json();
    // setLatestTransaction(ajson)
    setLatestTransaction(ajson.Records)
  }

  return (
    <Widget styleName="gx-order-history"
      title={
        <h2 className="h2 gx-text-capitalize gx-mb-0">
          10 Latest Transaction
        </h2>
      }

    >
      <div className="gx-table-responsive">
        <Table className="gx-table-no-bordered" columns={columns} dataSource={data} pagination={false} bordered={false}
          size="small" />


      </div>

    </Widget>
  );
};

export default LatestTransaction;
