import React, { useState, useEffect } from "react";
import { Col, Row } from "antd";
import { Bar, BarChart, ResponsiveContainer, Tooltip } from "recharts";
import Widget from "../../components/Widget/index"
import EcommerceStatus from "../../components/Metrics/EcommerceStatus";
import * as moment from 'moment';
import { forkJoin, mergeMap } from 'rxjs';
import "../../assets/styles/flip-card.css"

const PendapatanTahunIni = (props) => {
  const {latestTransaction, setLatestTransaction, getLatestTransaction} = props
  const tahunIni = moment().format('YYYY')
  const tahunLalu =moment().subtract(1,'years').format('YYYY')

  const [moneyThisYear, setMoneyThisYear] = useState()
  const [moneyLastYear, setMoneyLastYear] = useState()

  const tahun = latestTransaction && latestTransaction.map(row => ({
    nilai: row[3].stringValue,
    tanggal_transaksi: moment(row[1].stringValue).format('YYYY')
  }));
  const objTahunIni = tahun && tahun.filter(o => o.tanggal_transaksi === tahunIni);
  const currentTahunIni = objTahunIni && objTahunIni.map(v => Number(v.nilai))
    .reduce((sum, current) => sum + current, 0)

    const objTahunLalu = tahun && tahun.filter(o => o.tanggal_transaksi === tahunLalu);
    const currentTahunLalu = objTahunLalu && objTahunLalu.map(v => Number(v.nilai))
      .reduce((sum, current) => sum + current, 0)    

      console.log("latestTransaction",latestTransaction)

      console.log("tahun",tahun)

console.log("tahunIni",tahunIni)
  console.log("objTahunIni", objTahunIni);
  console.log("currentTahunIni", currentTahunIni);

  useEffect(() => {
    setMoneyThisYear(currentTahunIni);
  }, [currentTahunIni]);
  
  useEffect(() => {
    setMoneyLastYear(currentTahunLalu);
  }, [currentTahunLalu]);
  const formatter = new Intl.NumberFormat('id-ID', {
    style: 'currency',
    currency: 'IDR',
  
    // These options are needed to round to whole numbers if that's what you want.
    //minimumFractionDigits: 0, // (this suffices for whole numbers, but will print 2500.10 as $2,500.1)
    //maximumFractionDigits: 0, // (causes 2500.99 to be printed as $2,501)
  });
  return (
    <Col xl={4} lg={8} md={8} sm={12} xs={24} 
    className="e-commerce-status" >
      <div className="flip-card" >
        <div className="flip-card-inner" >
          <div className="flip-card-front" >
          <EcommerceStatus color="white"
  icon="revenue-new" title={formatter.format(currentTahunIni)} colorTitle="primary" 
  moneyThisYear={moneyThisYear} setMoneyThisYear ={setMoneyThisYear} moneyLastYear={moneyLastYear}  setMoneyLastYear={setMoneyLastYear} objTahunIni={objTahunIni}
              subTitle="Total Pendapatan (tahun Ini)" colorSubTitle="primary"  />
          </div>
          <div class="flip-card-back">
            <EcommerceStatus  icon="revenue-new" color="grey"  title={<div style={{marginBottom:"90px"}}>{formatter.format(currentTahunLalu)}</div>} colorTitle="dark" 
              // moneyToday={moneyToday} setMoneyToday ={setMoneyToday} moneyYesterday={moneyYesterday} setMoneyYesterday={setMoneyYesterday}
              subTitle="Total Pendapatan (tahun Lalu)" colorSubTitle="dark"/>
        </div>
        </div>
      </div>
    </Col>
  );
};

export default PendapatanTahunIni;
