    const [errorKab, setErrorKab] = useState(false);
    const [errorKec, setErrorKec] = useState(false);
    const [errorKel, setErrorKel] = useState(false);
    const [errorAlamat, setErrorAlamat] = useState(false);
    const [errorSumberData, setErrorSumberData] = useState(false);
    const [errorKategori, setErrorKategori] = useState(false);

    const [current, setCurrent] = useState(0);

    const [kabKotaId, setKabKotaId] = useState(null)
    const [kab, setKab] = useState("")

    const [kec, setKec] = useState("")
    const [kecId, setKecId] = useState(null)

    const [kel, setKel] = useState("")
    const [kelId, setKelId] = useState(null)

    const [alamatDetil, setAlamatDetil] = useState("")
    const [alamatLengkap, setAlamatLengkap] = useState("")

    const [regisDeviceAgent, setRegisDeviceAgent] = useState(
        {
            merchant_id: "",
            owner: "",
            nik: "",
            email: "",
            nama_usaha: "",
            alamat: "",
            kategori: "",
            data_source: dataSumber
        }

    );
    useEffect(
        () => {
            setRegisDeviceAgent({
                ...regisDeviceAgent,
                alamat: alamatDetil + " " + kel + " " + kec + " " + kab + " " + prov
            });
        },
        [alamatDetil], [prov]
    );
    useEffect(
        () => {
            setRegisDeviceAgent({
                ...regisDeviceAgent,
                kategori: category
            });
        },
        [category]
    );
    useEffect(
        () => {
            setRegisDeviceAgent({
                ...regisDeviceAgent,
                data_source: dataSumber
            });
        },
        [dataSumber]
    );

    useEffect(
        () => {
            if (provinceId) {
                setErrorProv("");
            }
            if (kabKotaId) {
                setErrorKab("");
            }
            if (kecId) {
                setErrorKec("");
            }
            if (kelId) {
                setErrorKel("");
            }
        },
        [provinceId, kabKotaId, kecId, kelId]
    );
    useEffect(
        () => {
            if (alamatDetil) {
                setErrorAlamat("");
            }
        },
        [alamatDetil]);
    useEffect(
        () => {
            if (dataSumber) {
                setErrorSumberData("");
            }
            if (category) {
                setErrorKategori("");
            }

        },
        [dataSumber, category]);
        const success = () => {
            message.success('Pendaftaran Berhasil');
          };
    const handleClickNext = () => {
        if (current === 0) {
            form.validateFields()
                .then(() => {
                    setCurrent(current + 1);
                })

                .catch((err) => console.log(err));
        }
        else if (current === 1 && prov && kab && kec && kel && alamatDetil) {
            form
                .validateFields()
                .then(() => {
                    setCurrent(current + 1);
                })
                .catch((err) => console.log(err));
        }
        else if (current === 2 && category && dataSumber) {
            form
                .validateFields()
                .then(() => {
                    setCurrent(current + 1);
                })
                .catch((err) => console.log(err));
        }

        if (current === 1 && provinceId === undefined) {
            setErrorProv(
                "   ❌ Pilih Provinsi terlebih dulu"
            );
        } else if (current === 1 && kabKotaId === undefined) {
            setErrorKab(
                "   ❌ Pilih Kabupaten terlebih dulu"
            );
        }
        else if (current === 1 && kecId === undefined) {
            setErrorKec(
                "   ❌ Pilih Kecamatan terlebih dulu"
            );
        } else if (current === 1 && kelId === undefined) {
            setErrorKel(
                "   ❌ Pilih Kelurahan terlebih dulu"
            );
        }
        else if (current === 1 && !alamatDetil) {
            setErrorAlamat(
                "   ❌ Isi alamat detil terlebih dulu"
            );
        }
        if (current === 2 && dataSumber === undefined) {
            setErrorSumberData(
                "   ❌ Pilih Sumber Data terlebih dulu"
            );
        }
        if (current === 2 && category === undefined) {
            setErrorKategori(
                "   ❌ Pilih Kategori terlebih dulu"
            );
        } else {
            console.log("");
        }
    };

    const handleClickPrev = () => {
        form
            .validateFields()
            .then(() => {
                // Here make api call of something else
                setCurrent(current - 1);
            })
            .catch((err) => console.log(err));
    };
    const handleChange = (e) => {
        setRegisDeviceAgent({ ...regisDeviceAgent, [e.target.name]: e.target.value });

    };

    const handleAlamat = (event) => {
        setAlamatDetil(event.target.value)
    }

    const handleChangeSelect = (value) => {
        setDataSumber(value);
    };
    const handleChangeKategori = (value) => {
        setCategory(value);
    };
    const handleFinish = async (values) => {
        try {
            const token = localStorage.getItem('token')
            const response = await fetch(
                "https://api.raspi-geek.com/v1/merchants",
                {
                    method: "POST",
                    headers: {
                        'x-api-key': '45N0Ujae7p9AWDs3ihDiU4ZDnvAMb9ZK4TXH0kCk',
                        'content-type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify(regisDeviceAgent),
                }
            );
            // const res = await response.json();
            success();
            history.push("/dashboard")
        } catch (err) {
            // console.log("error", err.message);
        }
    };


    return (
        <div style={{ width: "100%", margin: "40px auto" }}>
            <Steps current={current}>
                <Step key={0} title="Data Usaha" />
                <Step key={1} title="Alamat Usaha" />
                <Step key={2} title="Kategori Usaha" />
                <Step key={3} title="Selesai" />

            </Steps>
            <div style={{ margin: "100px 10px" }}>
                <Form
                    form={form}
                    onFinish={handleFinish}
                    initialValues={{
                        merchant_id: "", owner: "", nik: "", email: "", nama_usaha: "",
                        kelurahan: "", alamat: "", kategori: "", data_source: ""

                    }}
                >
                    {current === 0 && (
                        <div style={{ width: "90%" }} >
                            <Form.Item
                                style={{ margin: "40px" }}
                                name="merchant_id"
                                label="Device ID / Agent ID"
                                rules={[

                                    {
                                        required: true,
                                        message: 'IMEI atau id dari POS APP',
                                    },
                                ]}
                            >
                                <Input
                                    name="merchant_id"
                                    value={regisDeviceAgent.merchant_id}
                                    onChange={handleChange}
                                    placeholder="IMEI atau id dari POS APP" style={{ width: "85%", margin: "0 0 0 10px" }} />
                            </Form.Item>
                            <Form.Item
                                style={{ margin: "40px" }}
                                name="owner"
                                label="Owner"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Nama owner dari usaha',
                                    },
                                ]}
                            >
                                <Input
                                    name="owner"
                                    value={regisDeviceAgent.owner}
                                    onChange={handleChange}
                                    placeholder="Nama Owner dari usaha" style={{ width: "64%", margin: "0 0 0 97px" }} />
                            </Form.Item>
                            <Form.Item
                                style={{ margin: "40px" }}
                                name="nik"
                                label="NIK"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Nomor Induk Kepegawaian',
                                    },
                                ]}
                            >
                                <Input
                                    name="nik"
                                    value={regisDeviceAgent.nik}
                                    onChange={handleChange}
                                    placeholder="Nomor Induk Kepegawaian" style={{ width: "61%", margin: "0 0 0 117px" }} />
                            </Form.Item>
                            <Form.Item
                                style={{ margin: "40px" }}
                                name="email"
                                label="Email"
                                rules={[
                                    {
                                        type: 'email',
                                        message: 'Masukkan E-mail!',
                                    },
                                    {
                                        required: true,
                                        message: 'someone@someplace.com',
                                    },
                                ]}
                            >
                                <Input
                                    name="email"
                                    value={regisDeviceAgent.email}
                                    onChange={handleChange}
                                    placeholder="someone@someplace.com" style={{ width: "63%", margin: "0 0 0 105px" }} />
                            </Form.Item>
                            <Form.Item
                                style={{ margin: "40px" }}
                                name="nama_usaha"
                                label="Nama Usaha"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Brand dari usaha',
                                    },
                                ]}
                            >
                                <Input
                                    name="nama_usaha"
                                    value={regisDeviceAgent.nama_usaha}
                                    onChange={handleChange}
                                    placeholder="Brand dari usaha" style={{ width: "73%", margin: "0 0 0 56px" }} />
                            </Form.Item>
                        </div>
                    )}
                    {current === 1 && (
                        <div style={{ width: "90%", margin: "0 0 40px 0" }} >
                            <DataProvinsi prov={prov} setProv={setProv} errorProv={errorProv} setErrorProv={setErrorProv} provinceId={provinceId} setProvinceId={setProvinceId} />
                            {prov && (
                                <DataKabupaten kab={kab} setKab={setKab} errorKab={errorKab} setErrorKab={setErrorKab} provinceId={provinceId} kabKotaId={kabKotaId} setKabKotaId={setKabKotaId} />
                            )}
                            {kab && (
                                <DataKecamatan kel={kel} setKel={setKel} errorKec={errorKec} setErrorKec={setErrorKec} errorKel={errorKel} setErrorKel={setErrorKel} kecId={kecId} setKecId={setKecId} kelId={kelId} setKelId={setKelId} kec={kec} setKec={setKec} kabKotaId={kabKotaId} />
                            )}
                            {kel && (
                                <div style={{ margin: "40px 0 40px 0" }} >
                                    <h4 style={{ margin: "40px 0 20px 0", color: "#53586D" }}>Alamat Detail</h4>
                                    <Input
                                        name="alamat"
                                        value={alamatDetil}
                                        required
                                        onChange={handleAlamat}
                                        placeholder="Jalan, RT RW"
                                        style={{ width: "100%" }} />
                                    {errorAlamat && (
                                        <div style={{ color: "red", fontFamily:"NoirPro, sans-serif" }}>{errorAlamat}</div>
                                    )}
                                </div>

                            )}
                        </div>
                    )}

                    {current === 2 && (
                        <div style={{ width: "90%" }} >
                            <div style={{ margin: "40px 0" }} >
                                <h4 style={{ margin: "0px 0 20px 0", color: "#53586D" }}>Kategori Usaha</h4>
                                <Select
                                    // defaultValue="lucy" 
                                    style={{ margin: "40px 0 0 0" }}
                                    name="category"
                                    value={category}
                                    onChange={handleChangeKategori}
                                    rules={[
                                        { type: 'array', required: true, message: 'Pilih Kategori Usaha' },
                                    ]}
                                    style={{ width: "100%" }} >
                                    <Option value="Hotel">Hotel</Option>
                                    <Option value="Restoran">Restoran</Option>
                                    <Option value="Parkir">Parkir</Option>
                                    <Option value="Minimarket">Minimarket</Option>

                                </Select>
                                {errorKategori && (
                                    <div style={{ color: "red" }}>{errorKategori}</div>
                                )}
                            </div>
                            <div style={{ margin: "40px 0" }} >
                                <h4 style={{ margin: "0px 0 20px 0", color: "#53586D" }}>Sumber Data</h4>
                                <Select
                                    // defaultValue="lucy" 
                                    style={{ margin: "40px 0 0 0" }}
                                    name="dataSumber"
                                    value={dataSumber}
                                    onChange={handleChangeSelect}
                                    style={{ width: "100%" }} >
                                    <Option value="POS App">Agent</Option>
                                    <Option value="POS App">POS App</Option>
                                    <Option value="PDC">PDC</Option>
                                    <Option value="BDC">BDC</Option>
                                    <Option value="SDC">SDC</Option>

                                </Select>
                                {errorSumberData && (
                                    <div style={{ color: "red" }}>{errorSumberData}</div>
                                )}
                            </div>
                        </div>
                    )}
                      {current === 3 && (
                        <div style={{ width: "90%" }} >
                            <div style={{ margin: "40px 0" }} >
                                <h3 style={{ margin: " 0 auto",padding:"0",textAlign:"center", color: "#53586D" }}>Apakah Anda Yakin Data Sudah Benar?</h3>
                            </div>
                        </div>
                    )}

                    {current < 3 && (
                        <div style={{ textAlign: "center" }}>
                            <Button type="primary" onClick={handleClickNext}>Next step</Button>
                        </div>
                    )}
                    {current === 3 && (
                        <div style={{ textAlign: "center" }}>
                            <Button type="primary"
                                onClick={() => form.submit()}
                            >Submit</Button>
                        </div>
                    )}
                    {current < 4 && (
                        <div style={{ textAlign: "center" }}>
                            <Button onClick={handleClickPrev}>Previous step</Button>
                        </div>
                    )}
                </Form>

            </div>

        </div>
    );
}
export { MyStepForm };
