import React, { useState, useEffect } from "react";
import {
    PageHeader, Form, Input, Card, Cascader, Steps, Button, Select
} from "antd";
import { useHistory } from "react-router-dom";

import { StepPanel } from "./stepPanel";
import { options } from "less";
import DataProvinsi from "./dataProvinsi";
import DataKabupaten from "./dataKabupaten";
import DataKecamatan from "./dataKecamatan";
const { Option } = Select;

const { Step } = Steps;
// const { Item } = Form;

const kategori = [
    {
        value: 'Hotel',
        label: 'Hotel',
    },
    {
        value: 'Restoran',
        label: 'Restoran',
    },
    {
        value: 'Parkir',
        label: 'Parkir',
    },
    {
        value: 'Minimarket',
        label: 'Minimarket',
    },
]

function MyStepForm() {

    const history = useHistory();
    const [dataSumber, setDataSumber] = useState()
    const [category, setCategory] = useState()

    const [provinceId, setProvinceId] = useState(null)
    const [prov, setProv] = useState("")
    const [errorProv, setErrorProv] = useState(false);
    const [errorKab, setErrorKab] = useState(false);
    const [errorKec, setErrorKec] = useState(false);
    const [errorKel, setErrorKel] = useState(false);
    const [errorAlamat, setErrorAlamat] = useState(false);
    const [errorSumberData, setErrorSumberData] = useState(false);
    const [errorKategori, setErrorKategori] = useState(false);

    const [current, setCurrent] = useState(0);


    const [kabKotaId, setKabKotaId] = useState(null)
    const [kab, setKab] = useState("")
    const [kec, setKec] = useState("")
    const [kel, setKel] = useState("")
    const [alamatDetil, setAlamatDetil] = useState("")
    const [alamatLengkap, setAlamatLengkap] = useState("")

    console.log("alamatLengkap", alamatLengkap)
    const [regisPajak, setRegisPajak] = useState(
        {
            merchant_id: "",
            owner: "",
            nik: "",
            email: "",
            nama_usaha: "",
            alamat: "",
            kategori: "",
            data_source: dataSumber
        }

    );

    console.log("prov ======", prov)
    console.log("kab ======", kab)
    console.log("typeof kab xxxxxxxx", typeof kab)
    console.log("prov provinceId", provinceId)


    const [domisili_kabupaten, setDomisili_kabupaten] = useState()
    const prov_kab = JSON.stringify(domisili_kabupaten)
    const [form] = Form.useForm();
    useEffect(
        () => {
            setRegisPajak({
                ...regisPajak,
                alamat: alamatDetil + " " + kel + " " + kec + " " + kab + " " + prov
            });
        },
        [alamatDetil], [prov]
    );
    useEffect(
        () => {
            setRegisPajak({
                ...regisPajak,
                kategori: category
            });
        },
        [category]
    );
    useEffect(
        () => {
            setRegisPajak({
                ...regisPajak,
                data_source: dataSumber
            });
        },
        [dataSumber]
    );
    useEffect(
        () => {
            if(provinceId){
                setErrorProv("");
            }
           else if(kabKotaId){
                setErrorKab("");
            }
            else if(kec){
                setErrorKec("");
            }
            else if(kel){
                setErrorKel("");
            }
            else if(regisPajak.data_source){
                setErrorSumberData("");
            }
            else if(regisPajak.category){
                setErrorKategori("");
            }
        },
        [provinceId]
    );
    const handleClickNext = () => {

        if (current === 0 ) {
            form.validateFields()
                .then(() => {
                    setCurrent(current + 1);
                })
                .catch((err) => console.log(err));
        }
        else if (current === 1 && prov) {
            form
                .validateFields()
                .then(() => {
                    setCurrent(current + 1);
                })
                .catch((err) => console.log(err));
        }
        // else if (current === 1 && kab) {
        //     form
        //         .validateFields()
        //         .then(() => {
        //             // Here make api call of something else
        //             setCurrent(current + 1);
        //         })
        //         .catch((err) => console.log(err));
        // }

        if (current === 1 && provinceId === undefined) {
            setErrorProv(
                "   ❌ Pilih Provinsi terlebih dulu"
              );
        } else {
            setErrorProv("");
      }

        if (current === 1 && kabKotaId === undefined) {
            setErrorKab(
                "   ❌ Pilih Kabupaten terlebih dulu"
              );
        } else if (current === 1 && kec === undefined) {
            setErrorKec(
                "   ❌ Pilih Kecamatan terlebih dulu"
              );
        } else if (current === 1 && kel === undefined) {
            setErrorKec(
                "   ❌ Pilih Kelurahan terlebih dulu"
              );
        } else if (current === 2 && regisPajak.data_source === "") {
            setErrorKec(
                "   ❌ Pilih Sumber Data terlebih dulu"
              );
        } else if (current === 2 && regisPajak.kategori === "") {
            setErrorKec(
                "   ❌ Pilih Kategori terlebih dulu"
              );
        } else {
              setErrorKec("");
        }
    };
    const handleClickPrev = () => {
        form
            .validateFields()
            .then(() => {
                // Here make api call of something else
                setCurrent(current - 1);
            })
            .catch((err) => console.log(err));
    };
    const handleChange = (e) => {
        setRegisPajak({ ...regisPajak, [e.target.name]: e.target.value });

    };

    const handleAlamat = (event) => {
        setAlamatDetil(event.target.value)
    }

    const handleChangeSelect = (value) => {
        setDataSumber(value);
    };
    const handleChangeKategori = (value) => {
        setCategory(value);
    };
    const handleFinish = async (values) => {
        try {
            const token = localStorage.getItem('token')
            const response = await fetch(
                "https://api.raspi-geek.com/v1/merchants",
                {
                    method: "POST",
                    headers: {
                        'x-api-key': '45N0Ujae7p9AWDs3ihDiU4ZDnvAMb9ZK4TXH0kCk',
                        'content-type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                    body: JSON.stringify(regisPajak),
                }
            );
            // const res = await response.json();
            history.push("/dashboard")
        } catch (err) {
            // console.log("error", err.message);
        }

    };


    return (
        <div style={{ width: "100%", margin: "40px auto" }}>
            <Steps current={current}>
                <Step key={0} title="Data Usaha" />
