import React, { useState, useEffect } from "react";
import { Col, Row } from "antd";
import { Bar, BarChart, ResponsiveContainer, Tooltip } from "recharts";
import Widget from "../../components/Widget/index"
import EcommerceStatus from "../../components/Metrics/EcommerceStatus";
import * as moment from 'moment';
import "../../assets/styles/flip-card.css"
import { geekblue,blue } from '@ant-design/colors';
import {ArrowUpOutlined,ArrowDownOutlined } from '@ant-design/icons';

// import toRupiah from '@develoka/angka-rupiah-js';


const PendapatanHariIni = ( props) => {

  const { latestTransaction, setLatestTransaction } = props
  const [loading, setloading] = useState(true);

  useEffect(() => {
    getLatestTransaction();
  }, []);

  const token = localStorage.getItem('token')
  const now = moment().format('YYYY-MM-DD')
  const kemarin = moment().subtract(1, 'd').format('YYYY-MM-DD')

  const sToday = moment().startOf('day').format('YYYY-MM-DD HH:mm:ss')
  const eToday = moment().endOf('day').format('YYYY-MM-DD HH:mm:ss')
  const sYesterday = moment().subtract(1, 'd').startOf('day').format('YYYY-MM-DD HH:mm:ss')
  const eYesterday = moment().subtract(1, 'd').endOf('day').format('YYYY-MM-DD HH:mm:ss')

  const tanggal = latestTransaction && latestTransaction.map(row => ({
    nilai: row[3].stringValue,
    tanggal_transaksi: moment(row[1].stringValue).format('YYYY-MM-DD')
  }));

  const data = latestTransaction && latestTransaction.map(row => ({
    transaction_id: row[0].stringValue, merchant_id: row[2].stringValue,
    transaction_value: row[3].stringValue, tanggal_transaksi: row[1].stringValue
  }));
  console.log("latestTransaction >>>>>>>>>>>>>>>>>>", latestTransaction)


  // Tanggal 27 Total = 63.000
  // Tanggal 23 Total = 175.000
  // Tanggal 22 Total = 26.000.000
  // Total = 26.238.000
  // Total = 26.238.000



  // const length = latestTransaction && latestTransaction .map( v => v[3].stringValue).length()                                

  // console.log("length", latestTransaction && latestTransaction.length);

  // console.log("now", now);
  // console.log("ytanggal", tanggal);
  // console.log("kemarin", kemarin)

  const objToday = tanggal && tanggal.filter(o => o.tanggal_transaksi === now);
  // console.log("obj", objToday);
  const currentToday = objToday && objToday.map(v => Number(v.nilai))
    .reduce((sum, current) => sum + current, 0)
  // console.log("currentVal", currentVal);
  const objYesterday = tanggal && tanggal.filter(o => o.tanggal_transaksi === kemarin);
  // console.log("obj", objToday);
  const currentYesterday = objYesterday && objYesterday.map(v => Number(v.nilai))
    .reduce((sum, current) => sum + current, 0)
  // console.log("currentToday", currentToday);
  // const formatRupiah =(aa)=>{
const aa = currentToday
    const formatter = new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
    
      // These options are needed to round to whole numbers if that's what you want.
      //minimumFractionDigits: 0, // (this suffices for whole numbers, but will print 2500.10 as $2,500.1)
      //maximumFractionDigits: 0, // (causes 2500.99 to be printed as $2,501)
    });
    
    


  const getLatestTransaction = async (dataLatest) => {

    const headers = {
      'x-api-key': '45N0Ujae7p9AWDs3ihDiU4ZDnvAMb9ZK4TXH0kCk',
      'content-type': 'application/json',
      'Authorization': `Bearer ${token}`
    }

    const response = await fetch(
      "https://api.raspi-geek.com/v1/latestorder",

      { method: "GET", headers }
    );
    const ajson = await response.json();
    setLatestTransaction(ajson.Records)

  };



  return (
    <Col xl={4} lg={8} md={8} sm={12} xs={24} 
    className="e-commerce-status"
    // className={`ant-col ant-col-xs-24 ant-col-sm-12 ant-col-md-8 ant-c?ol-lg-8 ant-col-xl-4`}
    >
      <div className="flip-card" >
        <div className="flip-card-inner" >
          <div className="flip-card-front" >
            <EcommerceStatus color="green"
  icon="revenue-new" title={formatter.format(currentToday)} colorTitle="black" aa ={aa} currentYesterday ={currentYesterday}
              subTitle="Total Pendapatan (Hari Ini)" colorSubTitle="black"  />
          </div>
          <div class="flip-card-back">
            <EcommerceStatus color="grey" icon="revenue-new" title={formatter.format(currentYesterday)} colorTitle="black" 
              subTitle="Total Pendapatan (Kemarin)" colorSubTitle="black"/>
          </div>
        </div>
      </div>

      {/* {{currentVal}} */}
      {/* {latestTransaction && latestTransaction(data[0]['Records'][0][0]['longValue']).toLocaleString()} */}
    </Col>
  );
};

export default PendapatanHariIni;
