import React, { useState, useEffect } from "react";
import {
    PageHeader, Form, Input, Card, Cascader, Steps, Button, Select
} from "antd";
const { Option } = Select;

function DataProvinsi(props) {
    const {provinceId, setProvinceId,prov, setProv,errorProv, setErrorProv} =props

    const [provinsi, setProvinsi] = useState([{}])
    const [idProv, setIdProv] = useState({})


    console.log("provinsi",provinsi )
    console.log("prov",prov )
    console.log("idProv", idProv)

    useEffect(
        () => {
            setProvinceId(
                idProv.key
            );
        },
        [idProv.key]
    );
    useEffect(() => {
        getProvinsi();
    }, []);
    console.log("prov", prov)
    const getProvinsi = async () => {
        const response = await fetch(`http://www.emsifa.com/api-wilayah-indonesia/api/provinces.json`)
            .then(response => response.json())
            .then(provinces => {
                // console.log("provinces", provinces);
                setProvinsi({ data: provinces })
            }
            )
    };
    const handleChangeProvinsi = (value, key) => {
        setProv(value);
        setIdProv(key)
        // let text = value.target.value;
        // let regAngka = /^[0-9\b]+$/i;

        if (value === "" ) {
            setErrorProv(
                "   ❌ Pilih Provinsi terlebih dulu"
              );
        } else {
            setErrorProv("");

        }
        // console.log(text);
    };
    return (
        <div>
            <h4 style={{ margin: "20px 0 20px 0", color: "#53586D" }}>Provinsi</h4>
            <Select
                // defaultValue="lucy" 
                style={{ margin: "40px 0 0 0" }}
                name="provinsi"
                onChange={handleChangeProvinsi}
                value={prov}
                rules={[
                    {required: true, message: 'Pilih Provinsi Yang Ada' },
                ]}
                placeholder="Pilih Provinsi" required
                style={{ width: "100%" }} >
                {provinsi.data?.map((item) => (
                    <Option key={item.id} value={item.name}   
                    required
                     rules={[
                        { required: true, message: 'Pilih Provinsi Yang Ada' },
                    ]} >
                    </Option>
                ))}
            </Select>
            {!errorProv && (
                    <div className="error">{errorProv}</div>
                  )}
        </div>
    );
}

export default DataProvinsi;
